              <label>District  {user} is Located In:
              <input type="text" name="district" value={values.district} onChange={handleChange}/>
              </label>
           </>
             )}
  
          {(user !== 'admin'|| user == 'donor' || user == 'hospital' || user == 'bloodBank' ) && (
            <>
              {errors.telephone && <p>{errors.telephone}</p>}
              {validtelephone && <p>Telephone Number is Valid</p>}
              <label>
                Telephone Number:
                <input
                  type="text"
                  name="telephone"
                  value={values.telephone}
                  onChange={handleChange}
                />
              </label>

              {errors.address && <p>{errors.address}</p>}
              <label>
                Address:
                <input
                  type="text"
                  name="address"
                  value={values.address}
                  onChange={handleChange}
                />
              </label>
            </>
          )}
         {errors.password && <p>{errors.password}</p>}
          {validPassword && <p>Password is valid</p>}
          <label>
            Password:
            <input
              type="password"
              name="password"
              value={values.password}
              onChange={handleChange}
            />
          </label>
  
          <button type="submit">SignUp</button>
          </form>
  </>
    
  )
